function dfs_dialog(the_node, lst, serial, depth){
    if(the_node==null)return ;
    let item = msg.dialogs[the_node.index]
    item.depth = depth;
    item.new_idx = serial.join("-");
    if( depth==0 ){//|| the_node.parent==null ){
        item.serial = "";
    }else{

        if(the_node.parent == null){
            //node.warn(the_node);
        }else{
            item.serial=msg.dialogs[relay_dialogs[the_node.parent].index].serial+
                        " > "+
                        msg.dialogs[relay_dialogs[the_node.parent].index].title;
        }
    }

    lst.push(msg.dialogs[the_node.index]);
    if(the_node.first_child!=null){
        dfs_dialog(the_node.first_child, lst, serial.concat([1]), depth+1);
    }

    if(the_node.next_dialog!=null){
        serial[serial.length-1]+=1;
        dfs_dialog(the_node.next_dialog, lst, serial, depth);
    }
    return ;
}

