function transDoActions(sender_id,doAction){
	if(doAction===null){
        node.warn("API use WRONG");
        return null;
    }
    //node.warn(sender_id);
    //node.warn(doAction);
    let messageItem = {};
    if(doAction.type=="text"){
        messageItem={
            "text":doAction.text
          };
    }else if(doAction.type=="quick_reply"){
        //quick
        let quick_arr = [];
        for (let i in doAction.quick_reply){
            quick_arr.push({
                "content_type":"text",
                "title":doAction.quick_reply[i],
                "payload":doAction.quick_reply[i]
            });
        }
        messageItem={
			"text": doAction.quick_reply_text,
			"quick_replies":quick_arr
		  };
     }else if(doAction.type=="image"){
        //img
		messageItem={
            "attachment":{
              "type":"image", 
              "payload":{
                "url":doAction.image_url,
                "is_reusable":true
              }
            }
          };
    }else if(doAction.type=="file"){
        //file
     messageItem={
            "attachment":{
              "type":"file", 
              "payload":{
                "url":doAction.file_url
              }
            }
          };
	}else if(doAction.type=="buttons"){

        messageItem={
			"attachment":{
			  "type":"template",
			  "payload":{
				"template_type":"button",
				"text":doAction.text,
				"buttons":doAction.buttons
			  }
			}
		  }
    /*}else if(doAction.buttons!=null){
        //url

        var btn_arr = [];
        //node.warn("out"+doAction.buttons[].btn.length);
        for (var i in doAction.buttons.btn){
            //node.warn("in");
            if(doAction.buttons.btn[i].type=="link"){
              btn_arr.push({
                "type":"web_url",
                "url":doAction.buttons.btn[i].url,
                "title":doAction.buttons.btn[i].text
              });
            }
        }
        messageItem={
			"attachment":{
			  "type":"template",
			  "payload":{
				"template_type":"button",
				"text":doAction.buttons.text,
				"buttons":btn_arr
			  }
			}
		  }*/

    }else if(doAction.type=="promote_video"){
        messageItem={
            "attachment": {
              "type": "template",
              "payload": {
                 "template_type": "media",
                 "elements": [
                    {
                       "media_type": "video",
                       "url":"https://www.facebook.com/TOYOTA.Taiwan/videos/1933883716681658/",
                       "buttons": [
                             {
                                "type": "web_url",
                                "url": "https://www.facebook.com/TOYOTA.Taiwan/videos/1933883716681658/",
                                "title": "你，聽見那引擎聲了嗎？",
                             }
                        ]
                    }
                 ]
              }
            }    
        }
    }else if(doAction.type=="generic_template"){
        if(doAction.elements&&doAction.elements.length==1&&doAction.elements[0].buttons&&doAction.elements[0].buttons.length==1&&doAction.elements[0].buttons[0].type=="web_url"){
            doAction.elements[0].default_action={
