function exist_str_btn(text){

    if(text && text.split("1.").length>1){
        if(!text.split("1.")[1].startsWith("pdf")){
            return true;
        }
    }
    return false;
}

function combine_btn(text){

    let buttonTitle = "";
    let buttonAssign=[];
    buttonTitle = text.split("1.")[0];
    buttonTitle = buttonTitle==""?"　":buttonTitle;

    for(var i=1;i<10;i++){
        if(text.split(i+".").length>1){
            let fetchStr = text
            		    .split(i+".")[1]
            		    .split((i+1)+".")[0];
            buttonAssign.push(fetchStr.trim());
        }
    }

    return {
        "type": "quick_reply",
        "quick_reply": buttonAssign,
        "quick_reply_text": buttonTitle
    };
